import React, { Component } from 'react';
import './App.css';

class App extends Component {

  render(){
    return(
      <div>
        <Macgyver largura={400} altura={250} />
        <Macgyver largura={200} altura={120} />
        <Macgyver largura={300} altura={180} />
      </div>
    );
  }
}

export default App;


class Macgyver extends Component {
  render(){

    let img = 'https://vejasp.abril.com.br/wp-content/uploads/2016/12/ads_macgyver1.jpg';

    return(
        <img src={img} width={this.props.largura} height={this.props.altura} />
    );
  }
}
